async def pspam(e):
    usage = f"π— π—Όπ—±π‚π—Ήπ—² π—΅π—®π—Ίπ—²: π—£π—Όπ—Ώπ—»π—¦π—½π—®π—Ί\n  Β» {hl}pspam <count>"
    if e.sender_id in SUDO_USERS:
        if e.text[0].isalpha() and e.text[0] in ("/", "#", "@", "!"):
            return await e.reply(usage, parse_mode=None, link_preview=None )
        altron = ("".join(e.text.split(maxsplit=1)[1:])).split(" ", 1)
        if len(altron) == 1:
            counter = int(altron[0])
            if int(e.chat_id) in GROUP:
                await e.reply("Β» κ±α΄Κ€Κ€Κ, α΄›ΚΙκ± Ικ± α΄€Κα΄›Κ€α΄Ι΄ α΄Κ€α΄α΄›α΄‡α΄„α΄›α΄‡α΄… ΙΆΚ€α΄α΄α΄.", parse_mode=None, link_preview=None )
            else:
                 porrn = random.choice(PORMS)
                 for _ in range(counter):
                    alt = await e.client.send_file(e.chat_id, porrn)
                    await gifspam(e, alt) 
                    await asyncio.sleep(0.1)
        else:
            await e.reply(usage)

